// src/App.js
import React, { useState } from "react"; // Import useState for state management
import Navbar from "./components/Navbar";
import Hero from "./components/Hero";
import Projects from "./components/Projects";
import Experience from "./components/Experience";
import Contact from "./components/Contact";
import Resume from "./components/Resume"; // Assuming you have a separate Resume.js component

function App() {
  // State to control the visibility of the Resume component
  const [showResume, setShowResume] = useState(false);

  // Function to toggle the showResume state
  const handleShowResume = () => {
    setShowResume(!showResume);
  };

  return (
    // Applied base dark theme to the main div
    <div className="min-h-screen bg-gray-900 text-white font-sans">
      <Navbar />
      {/* Pass showResume state and the handler to the Hero component */}
      <Hero showResume={showResume} handleShowResume={handleShowResume} />
      {/* Render other sections */}
      <Projects />
      <Experience />
      <Contact />
      {/* Conditionally render the Resume component based on showResume state */}
      {showResume && <Resume />}
    </div>
  );
}

export default App;
